            for (let i = 0; i < response.egg_groups; i++) {
                let obj = { name: response.egg_groups[i].name };
                array.push(obj);
            }
        });
        //console.log(array);
        return array;
    }
    listPokemons() {
        this.params.limit = this.pageSize;
        this.params.offset = this.page * this.pageSize - this.pageSize;
        this.getData();
        //console.log(this.params);
    }
}
